def prime_factors(n):
    factors = []
    d = 2
    while n > 1:
        while n % d == 0:
            factors.append(d)
            n /= d
        d = d + 1
    return factors

def wordFreq(str): 
    # break the string into list of words  
    str_list = str.split(" ") 

    # give dictionary for word frequency 
    freq = {} 

    for word in str_list: 
        if (word in freq): 
            freq[word] += 1
        else: 
            freq[word] = 1

    # print words with frequency  
    for key, value in freq.items(): 
        print ("% d : % d"%(key, value))

